// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Token.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import 'base64-sol/base64.sol';

contract Charities is ERC721 {

    struct Charity {
        // whether the charity has registered
        bool isRegistered;
        // name of the charity
        string name;
        // total amount donated to this charity
        uint amount;
    }

    struct Fund {
        address owner;
        string title;
        string description;
        uint start;
        uint deadline;
        uint goal;
        uint balance;
        string imageURI;
        bool claimed;
    }

    struct NFTFund {
        string title;
        string description;
        uint amountDonated;
        string imageURI;
        uint id;
    }

    mapping (address => mapping(uint => uint)) public fundsDonated;
    
    mapping (address => Charity) public allCharities;
    // list of registered charities
    address[] public charityList;

    uint public fundCounter;

    Fund[] public fundList;

    mapping (uint => Fund) public allFunds;

    IToken public token;

    address private _owner;



    // NFTs tokenId => that NFTs attributes.
    mapping(uint256 => NFTFund) public nftAttributes;

    uint public tokenCounter;

    // track each users NFTs
    mapping(address => NFTFund[]) public userNFTs;

    constructor() ERC721("NFTReward", "REW") {
        _owner = msg.sender;
        token = new Token();
        tokenCounter = 0;
        fundCounter = 0;
    }

    // NFT functions

    // returns the tokenURI for the given tokenId

    function tokenURI(uint _tokenId) public view override returns (string memory) {
        NFTFund memory fund = nftAttributes[_tokenId];
        bytes memory image = abi.encodePacked("ipfs://", fund.imageURI);

        return string(
            abi.encodePacked(
                "data:application/json;base64,",
                Base64.encode(
                    bytes(
                        abi.encodePacked(
                            '{"name": "',
                            fund.title,
                            ' -- Charity NFT #: ',
                            Strings.toString(_tokenId),
                            '", "description": "',
                            fund.description,
                            '", "image": "',
                            image,
                            '", "attributes": [{"trait_type": "Amount Donated", "value": ',fund.amountDonated,'}]}'
                            '"}'
                        )
                    )
                )
            )
        );
    }

    // Get all NFTs owned by a user
    function getNFTs(address owner) public view returns (NFTFund[] memory) {
        return userNFTs[owner];
    }

    // Fundraise

    function createFund(string memory title, string memory description, uint start, uint end, uint goal, string memory imageURI) public {
        Fund storage f = allFunds[fundCounter];
        f.owner = msg.sender;
        f.title = title;
        f.description = description;
        f.start = start;
        f.deadline = end;
        f.goal = goal;
        f.balance = 0;
        f.imageURI = imageURI;
        f.claimed = false;
        fundList.push(f);
        fundCounter++;
    }

    function pledge(uint fundNum, uint numOfTokens) public payable {
        require(block.timestamp > allFunds[fundNum].start, "Fund hasn't started");
        require(block.timestamp < allFunds[fundNum].deadline, "Deadline has passed");
        uint allowance = token.allowance(msg.sender, address(this));
        require(allowance >= numOfTokens, "Not approved to transfer this many tokens");
        token.transferFrom(msg.sender, address(this), numOfTokens);

        allFunds[fundNum].balance += numOfTokens;
        fundsDonated[msg.sender][fundNum] += numOfTokens;
    }

    function refund(uint fundNum) public {
        require(allFunds[fundNum].balance < allFunds[fundNum].goal, "Goal has been met");
        require(block.timestamp > allFunds[fundNum].deadline, "Deadline has not been reached");
        uint amount = fundsDonated[msg.sender][fundNum];
        require(amount > 0, "You have not donated to this fund");
        fundsDonated[msg.sender][fundNum] = 0;
        allFunds[fundNum].balance -= amount;
        token.transfer(msg.sender, amount);
    }

    function claim(uint fundNum) public {
        require(allFunds[fundNum].claimed == false, "You have already claimed the funds");
        require(allFunds[fundNum].balance >= allFunds[fundNum].goal, "Goal has not been met");
        require(block.timestamp > allFunds[fundNum].deadline, "Deadline has not been reached");
        require(allFunds[fundNum].owner == msg.sender, "You are not the owner of this fund");
        allFunds[fundNum].claimed = true;
        token.transfer(msg.sender, allFunds[fundNum].balance);
    }

    function claimNFT(uint fundNum) public {
        require(allFunds[fundNum].balance >= allFunds[fundNum].goal, "Goal has not been met");
        require(block.timestamp > allFunds[fundNum].deadline, "Deadline has not been reached");
        uint amount = fundsDonated[msg.sender][fundNum];
        require(amount > 0, "You have not donated to this fund");
        fundsDonated[msg.sender][fundNum] = 0;
        string memory _title = allFunds[fundNum].title;
        string memory _description = allFunds[fundNum].description;
        string memory _imageURI = allFunds[fundNum].imageURI;
        
        uint newItemId = tokenCounter;
		_safeMint(msg.sender, newItemId);
        
        nftAttributes[newItemId].title = _title;
        nftAttributes[newItemId].description = _description;
        nftAttributes[newItemId].amountDonated = amount;
        nftAttributes[newItemId].imageURI = _imageURI;
        nftAttributes[newItemId].id = newItemId;

        userNFTs[msg.sender].push(nftAttributes[newItemId]);

        tokenCounter++;
    }

    // Token Shop

    // user can buy tokens
    function buy() payable public {
        // currently 10000 token == 1 ether
        uint amountTobuy = msg.value * 10000;
        uint contractBalance = token.balanceOf(address(this));
        require(amountTobuy > 0, "You need to send some ether");
        require(amountTobuy <= contractBalance, "Not enough tokens in the reserve");
        token.transfer(msg.sender, amountTobuy);
    }

    function sell(uint numOfTokens) public {
        require(numOfTokens > 0, "You need to sell 0 tokens");
        uint allowance = token.allowance(msg.sender, address(this));
        require(allowance >= numOfTokens, "Not approved to transfer this many tokens");
        
        token.transferFrom(msg.sender, address(this), numOfTokens);
        uint amountToSell = numOfTokens / 10000;
        payable(msg.sender).transfer(amountToSell);
    }

    // register a new charity
    function newCharity(address beneficiary, string memory name) public {
        require(msg.sender == beneficiary || msg.sender == _owner);
        require(!isRegistered(beneficiary), "You are already registered");
        charityList.push(beneficiary);
        
        Charity storage c = allCharities[beneficiary];
        c.isRegistered = true;
        c.name = name;
    }

    // the sender can send a donation to a charity
    function donateToCharity(address beneficiary, uint numOfTokens) public {
        require(numOfTokens > 0, "You need to donate more than 0 tokens");
        require(isRegistered(beneficiary), "Charity is not registered");
        uint allowance = token.allowance(msg.sender, address(this));
        require(allowance >= numOfTokens, "Not approved to transfer this many tokens");
        token.transferFrom(msg.sender, address(this), numOfTokens);

        allCharities[beneficiary].amount += numOfTokens;
    }

    // withdraw/send a given number of tokens to a charity
    function withdraw(address beneficiary, uint numOfTokens) public {
        require(msg.sender == beneficiary, "You are not the owner of the charity");

        uint contractBalance = token.balanceOf(address(this));
        require(numOfTokens > 0, "You need to transfer more than 0 tokens");
        require(numOfTokens <= allCharities[beneficiary].amount, "You do not have that many tokens");
        require(numOfTokens <= contractBalance, "Not enough tokens in the reserve");
        token.transfer(msg.sender, numOfTokens);

        allCharities[beneficiary].amount -= numOfTokens;
    }

    function getTokenBalance() public view returns (uint) {
        return token.balanceOf(msg.sender);
    }

    function getTokenAddress() public view returns (address) {
        return address(token);
    }

    function getOwner() public view returns (address) {
        return _owner;
    }

    function getCharity(address beneficiary) public view returns (Charity memory) {
        return allCharities[beneficiary];
    }

    function getCharityList() public view returns (address[] memory) {
        return charityList;
    }

    function getFund(uint fundNum) public view returns (Fund memory) {
        return allFunds[fundNum];
    }

    function getFundList() public view returns (Fund[] memory) {
        return fundList;
    }

    function getAmountDonatedToFund(address beneficiary, uint fundNum) public view returns (uint tokens) {
        return fundsDonated[beneficiary][fundNum];
    }

    function isRegistered(address beneficiary) public view returns (bool registered) {
        return allCharities[beneficiary].isRegistered;
    }

    function getTokensRecieved(address beneficiary) public view returns (uint tokens) {
        return allCharities[beneficiary].amount;
    }
    
    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    // Fallback function is called when msg.data is not empty
    fallback() external payable {}
}
