// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./Token.sol";

contract Charities {

    struct Charity {
        // whether the charity has registered
        bool isRegistered;
        // name of the charity
        string name;
        // total amount donated to this charity
        uint amount;
    }

    struct Fund {
        address owner;
        string title;
        string description;
        uint start;
        uint deadline;
        uint goal;
        uint balance;
    }

    mapping (address => mapping(uint => uint)) public fundsDonated;
    
    mapping (address => Charity) public allCharities;
    // list of registered charities
    address[] public charityList;

    uint public fundCounter;

    Fund[] public fundList;

    IToken public token;

    address private _owner;

    constructor() {
        _owner = msg.sender;
        token = new Token();
        fundCounter = 0;
    }

    function createFund(string memory title, string memory description, uint start, uint end, uint goal) public {
        Fund memory f;
        f.owner = msg.sender;
        f.title = title;
        f.description = description;
        f.start = start;
        f.deadline = end;
        f.goal = goal;
        f.balance = 0;
        fundList.push(f);
        fundCounter++;
    }

    function pledge(uint fundNum, uint numOfTokens) public payable {
        require(block.timestamp >= fundList[fundNum].start, "Fund hasn't started");
        require(block.timestamp <= fundList[fundNum].deadline, "Deadline has passed");
        uint allowance = token.allowance(msg.sender, address(this));
        require(allowance >= numOfTokens, "Not approved to transfer this many tokens");
        token.transferFrom(msg.sender, address(this), numOfTokens);

        fundList[fundNum].balance += numOfTokens;
        fundsDonated[msg.sender][fundNum] += numOfTokens;
    }

    function refund(uint fundNum) public {
        require(address(this).balance < fundList[fundNum].goal, "Goal has been met");
        require(block.timestamp > fundList[fundNum].deadline, "Deadline has not been reached");
        uint amount = fundsDonated[msg.sender][fundNum];
        require(amount > 0, "You have not donated to this fund");
        fundsDonated[msg.sender][fundNum] = 0;
        token.transfer(msg.sender, amount);      
    }

    function claim(uint fundNum) public {
        require(address(this).balance < fundList[fundNum].goal, "Goal has been met");
        require(block.timestamp > fundList[fundNum].deadline, "Deadline has not been reached");
        require(fundList[fundNum].owner == msg.sender, "You are not the owner of this fund");
        token.transfer(msg.sender, fundList[fundNum].balance);
    }

    // user can buy tokens
    function buy() payable public {
        // currently 10000 token == 1 ether
        uint amountTobuy = msg.value * 10000;
        uint contractBalance = token.balanceOf(address(this));
        require(amountTobuy > 0, "You need to send some ether");
        require(amountTobuy <= contractBalance, "Not enough tokens in the reserve");
        token.transfer(msg.sender, amountTobuy);
    }

    function sell(uint numOfTokens) public {
        require(numOfTokens > 0, "You need to sell 0 tokens");
        uint allowance = token.allowance(msg.sender, address(this));
        require(allowance >= numOfTokens, "Not approved to transfer this many tokens");
        
        token.transferFrom(msg.sender, address(this), numOfTokens);
        uint amountToSell = numOfTokens / 10000;
        payable(msg.sender).transfer(amountToSell);
    }

    // register a new charity
    function newCharity(address beneficiary, string memory name) public {
        require(msg.sender == beneficiary || msg.sender == _owner);
        require(!isRegistered(beneficiary), "You are already registered");
        charityList.push(beneficiary);
        
        Charity storage c = allCharities[beneficiary];
        c.isRegistered = true;
        c.name = name;
    }

    // the sender can send a donation to a charity
    function donateToCharity(address beneficiary, uint numOfTokens) public {
        require(numOfTokens > 0, "You need to donate more than 0 tokens");
        require(isRegistered(beneficiary), "Charity is not registered");
        uint allowance = token.allowance(msg.sender, address(this));
        require(allowance >= numOfTokens, "Not approved to transfer this many tokens");
        token.transferFrom(msg.sender, address(this), numOfTokens);

        allCharities[beneficiary].amount += numOfTokens;
    }

    // withdraw/send a given number of tokens to a charity
    function withdraw(address beneficiary, uint numOfTokens) public {
        require(msg.sender == beneficiary, "You are not the owner of the charity");

        uint contractBalance = token.balanceOf(address(this));
        require(numOfTokens > 0, "You need to transfer more than 0 tokens");
        require(numOfTokens <= allCharities[beneficiary].amount, "You do not have that many tokens");
        require(numOfTokens <= contractBalance, "Not enough tokens in the reserve");
        token.transfer(msg.sender, numOfTokens);

        allCharities[beneficiary].amount -= numOfTokens;
    }

    function getTokenBalance() public view returns (uint) {
        return token.balanceOf(msg.sender);
    }

    function getTokenAddress() public view returns (address) {
        return address(token);
    }

    function getOwner() public view returns (address) {
        return _owner;
    }

    function getCharity(address beneficiary) public view returns (Charity memory) {
        return allCharities[beneficiary];
    }

    function getCharityList() public view returns (address[] memory) {
        return charityList;
    }

    function getFundList() public view returns (Fund[] memory) {
        return fundList;
    }

    function isRegistered(address beneficiary) public view returns (bool registered) {
        return allCharities[beneficiary].isRegistered;
    }

    function getTokensRecieved(address beneficiary) public view returns (uint tokens) {
        return allCharities[beneficiary].amount;
    }
    
    // Function to receive Ether. msg.data must be empty
    receive() external payable {}

    // Fallback function is called when msg.data is not empty
    fallback() external payable {}
}
