// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

// Interface for the ERC20 Token
interface IToken {

    function totalSupply() external view returns (uint);
    function balanceOf(address account) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function transfer(address recipient, uint amount) external returns (bool);
    function approve(address spender, uint amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint amount) external returns (bool);


    // MUST trigger when tokens are transferred, including zero value transfers.
    // A token contract which creates new tokens SHOULD trigger a Transfer event with the _from address set to 0x0 when tokens are created.
    event Transfer(address indexed _from, address indexed _to, uint _value);

    // MUST trigger on any successful call to approve(address _spender, uint _value)
    event Approval(address indexed _owner, address indexed _spender, uint _value);
}

// ERC20 Token
contract Token is IToken {
    string public _name = "Charity Token";
    string public _symbol = "CHT";
    uint8 public _decimals = 18;
    uint public _totalSupply = 0;

    mapping (address => uint) private _balances;
    mapping (address => mapping(address => uint)) private _allowance;

    constructor() {
        _mint(msg.sender, 1000000000 * (10 ** decimals())); // mint 1B tokens
    }

    // Returns the name of the token
    function name() public view returns (string memory) {
        return _name;
    }

    // Returns the symbol of the token
    function symbol() public view returns (string memory) {
        return _symbol;
    }

    // Returns the number of decimals the token uses - e.g. 8, means to divide the token amount by 100000000 to get its user representation.
    function decimals() public view returns (uint8) {
        return _decimals;
    }

    // Returns the total token supply
    function totalSupply() public view returns (uint) {
        return _totalSupply;
    }

    // Returns the account balance of another account with address _owner
    function balanceOf(address _owner) public view returns (uint) {
        return _balances[_owner];
    }

    // Transfers _value amount of tokens to address _to
    function transfer(address _to, uint _value) public returns (bool) {
        require(_balances[msg.sender] >= _value);
        // _value is subtracted from the sender and added to _to
        _balances[msg.sender] = _balances[msg.sender] - _value;
        _balances[_to] = _balances[_to] + _value;
        // transfer event is emitted
        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    // Transfers _value amount of tokens from address _from to address _to
    function transferFrom(address _from, address _to, uint _value) public returns (bool) {
        require(_balances[_from] >= _value);
        require(_allowance[_from][msg.sender] >= _value);
        // _value is subtracted from _from and added to _to
        _balances[_from] = _balances[_from] - _value;
        _balances[_to] = _balances[_to] + _value;
        // allowance for the sender is updated
        _allowance[_from][msg.sender] = _allowance[_from][msg.sender] - _value;
        // transfer event is emitted
        emit Transfer(msg.sender, _to, _value);
        return true;
    }

    // Allows _spender to withdraw from your account multiple times, up to the _value amount
    function approve(address _spender, uint _value) public returns (bool) {
        require(_spender != address(0));
        // allowance is updated with the new _value that account is allowed to transfer
        _allowance[msg.sender][_spender] = _value;
        // approval event is emitted
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    // Returns the amount which _spender is still allowed to withdraw from _owner
    function allowance(address _owner, address _spender) public view returns (uint) {
        return _allowance[_owner][_spender];
    }

    function _mint(address account, uint amount) internal {
    _totalSupply = _totalSupply + amount;
    _balances[account] = _balances[account] + amount;
    emit Transfer(address(0), account, amount);
    }

}
